mapper.select:
select * from MediChart
order by created_date desc
limit #{startRow}, #{pageSize};

mapper.insert:
INSERT INTO MediChart ( name, date_of_birth, age, gender, diagnosis, admission_date, discharge_date, condition_and_opinion, department_doctor
) VALUES (
#{name}, #{date_of_birth}, #{age}, #{gender}, #{diagnosis}, #{admission_date}, #{discharge_date}, #{condition_and_opinion}, #{department_doctor}
);
